import React, { useEffect, useState } from 'react';
import API from '../api';
import { Link } from 'react-router-dom';

export default function AdminDashboard(){
  const [pets, setPets] = useState([]);
  useEffect(()=> { API.get('/pets').then(r=> setPets(r.data)); }, []);
  return (
    <div>
      <h2>Admin Dashboard</h2>
      <Link to="/admin/pets/edit/new" className="btn btn-success mb-3">Add New Pet</Link>
      <table className="table">
        <thead><tr><th>Name</th><th>Type</th><th>Breed</th><th>Actions</th></tr></thead>
        <tbody>
          {pets.map(p=> (
            <tr key={p._id}>
              <td>{p.name}</td>
              <td>{p.type}</td>
              <td>{p.breed}</td>
              <td>
                <Link className="btn btn-sm btn-primary me-2" to={`/admin/pets/edit/${p._id}`}>Edit</Link>
                <button className="btn btn-sm btn-danger" onClick={async ()=> {
                  if(!window.confirm('Delete pet?')) return;
                  await API.delete(`/pets/${p._id}`); setPets(pets.filter(x=>x._id!==p._id));
                }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
